mapper.delete:
DELETE
FROM advisor_blocked_times
WHERE advisor_id = #{advisorId}
  AND date = #{date}

mapper.select:
SELECT a.advisor_id as id, u.name as name, a.profile_image_url as profileImageUrl, adi.preferred_trade_style as preferredStyle, adi.short_intro as shortIntro, adi.avg_rating as averageRating, adi.review_counting as reviewCount, a.consultation_fee as consultationFee, a.is_approved as isApproved, a.created_at as createdAt FROM advisor a INNER JOIN users u ON a.advisor_id = u.id LEFT JOIN advisor_detail_info adi ON a.advisor_id = adi.advisor_id WHERE 1=1 AND a.is_approved = 1
AND adi.preferred_trade_style IN
#{style}
AND a.advisor_id > #{cursor}
ORDER BY
adi.avg_rating DESC, a.advisor_id ASC
adi.review_counting DESC, a.advisor_id ASC
LIMIT #{pageSize}

SELECT start_time as startTime,
       end_time   as endTime
FROM advisor_blocked_times
WHERE advisor_id = #{advisorId}
  AND date = #{date}
ORDER BY start_time

SELECT TIME_FORMAT(start_time, '%H:%i') as timeSlot
FROM advisor_blocked_times
WHERE advisor_id = #{advisorId}
  AND date = #{date}
ORDER BY start_time
