mapper.select:
SELECT cp.id as postId, cp.title, CASE WHEN u.role = 'ADVISOR' THEN u.name ELSE u.nickname END as authorName, u.role as authorRole, cp.category, cp.view_count as viewCount, cp.comment_count as commentCount, DATE_FORMAT(cp.created_at, '%Y-%m-%dT%H:%i:%s+09:00') as createdAt FROM community_posts cp INNER JOIN users u ON cp.user_id = u.id
cp.deleted_at IS NULL
AND cp.category = #{category}
ORDER BY cp.created_at DESC LIMIT #{pageRequest.limitPlusOne} OFFSET #{pageRequest.offset}
