mapper.select:
SELECT EXISTS(SELECT 1
              FROM advisor_blocked_times abt
              WHERE abt.advisor_id = #{advisorUserId}
                AND abt.date = #{date}
                AND (
                (#{startTime} >= abt.start_time AND #{startTime} < abt.end_time)
                  OR (#{endTime} > abt.start_time AND #{endTime} <= abt.end_time)
                  OR (#{startTime} <= abt.start_time AND #{endTime} >= abt.end_time)
                ))

SELECT EXISTS(SELECT 1
              FROM consultation_sessions cs
              WHERE cs.advisor_id = #{advisorUserId}
                AND cs.date = #{date}
                AND cs.status IN ('PENDING', 'APPROVED')
                AND (
                (#{startTime} >= cs.start_time AND #{startTime} < cs.end_time)
                  OR (#{endTime} > cs.start_time AND #{endTime} <= cs.end_time)
                  OR (#{startTime} <= cs.start_time AND #{endTime} >= cs.end_time)
                ))

SELECT role
FROM users
WHERE id = #{userId}
